import React from 'react';


const getSeason = (lat, month) => {
	if (lat > 0) {
		if(month > 1 && month < 5) {
			return "spring";
		} else if (month > 4 && month < 8) {
			return "summer";
		} else if (month > 7 && month < 10) {
			return "fall";
		} else {
			return "winter";
		}
	} else if (lat < 0) {
		if(month > 1 && month < 5) {
			return "fall";
		} else if (month > 4 && month < 8) {
			return "winter";
		} else if (month > 7 && month < 10) {
			return "spring";
		} else {
			return "summer";
		}
	}
}

const SeasonDisplay = props => {
	const season = getSeason(props.lat, new Date().getMonth());

	const seasonMessage = (function seasonSwitch(season) {
		switch(season) {
			case "winter":
				return "The Weather Outside is Frightful";
				break;
			case "spring":
				return "Mother Earth has Awoken";
				break;
			case "summer":
				return "Surf's Up, Son!!!";
				break;
			case "fall":
				return "It's Fall, Bitches!!!";
				break;
			default:
				return "Not Working..."
		}
	})(season);

		const seasonIcon = (function seasonLogo(season) {
			let icon;
			switch(season) {
				case "winter":
					icon = 'snowflake';
					break;
				case "spring":
					icon = 'umbrella'
					break;
				case "summer":
					icon = 'sun';
					break;
				case "fall":
					icon = 'leaf'
					break;
				default:
					icon = 'question circle'
			}
		})(season);

	return (
		<div>


			<h1>{season}</h1>
			<h2>{seasonMessage}</h2>



		</div>


);
};

export default SeasonDisplay;
