import React from 'react';


const getSeason = (lat, month) => {
	if (lat > 0) {
		if(month > 1 && month < 5) {
			return "spring";
		} else if (month > 4 && month < 8) {
			return "summer";
		} else if (month > 7 && month < 10) {
			return "fall";
		} else {
			return "winter";
		}
	} else if (lat < 0) {
		if(month > 1 && month < 5) {
			return "fall";
		} else if (month > 4 && month < 8) {
			return "winter";
		} else if (month > 7 && month < 10) {
			return "spring";
		} else {
			return "summer";
		}
	}
}

const SeasonDisplay = props => {
	const season = getSeason(props.lat, new Date().getMonth());

	const seasonTheme = (function seasonLogo(season) {
		switch(season) {
			case "winter":
				return "The Weather Outside is Frightful";
				var icon = 'snowflake';
				break;
			case "spring":
				return "Mother Earth has Awoken";
				break;
			case "summer":
				return "Surf's Up, Son!!!";
				icon = 'sun';
				break;
			case "fall":
				return "It's Fall, Bitches!!!";

				break;
			default:
				return "Not Working..."
		}
	})(season);

	return (
		<div>
			<i className={`${icon} icon`}></i>
			<h1>{season}</h1>
			<h2>{seasonTheme}</h2>
			<i className={`${icon} icon`}></i>

		</div>


);
};

export default SeasonDisplay;
