import React from 'react';
import ReactDOM from 'react-dom';
import SeasonDisplay from './SeasonDisplay';

// This app focuses on `class`-based vs `functional` components in react and some of the best instances to use for each.

/* ***Rules of `Class` Components:
	Must be a JavaScript class;
	Must `extend` (subclass) React.Component;
	Must Define a `render` method that will `return` some JSX
*/

/* *** `App` written as a `functional` component
const App = () => {
	return (
		<div>
			<h1>Hi Kieran</h1>
			<SeasonDisplay />
		</div>
	);
};
*/

// `App` written as `class`- based component:
class App extends React.Component {
	render() {
		// use built-in browser function to get `geolocation` for user
		window.navigator.geolocation.getCurrentPosition(
			(position) => console.log(position),

			// make sure to `console.log(error)` any time any errors may pop up in your code
			(error) => console.log(error)
		);


		return (
			<div>
				Latitude
			</div>
		);
	};
};

ReactDOM.render(
	<App />,
	document.querySelector('#root')
);
