			return "spring";
		} else if (month > 4 && month < 8) {
			return "summer";
		} else if (month > 7 && month < 10) {
			return "fall";
		} else {
			return "winter";
		}
	} else if (lat < 0) {
		if(month > 1 && month < 5) {
			return "fall";
		} else if (month > 4 && month < 8) {
			return "winter";
		} else if (month > 7 && month < 10) {
			return "spring";
		} else {
			return "summer";
		}
	}
}

