import React from 'react';


const getSeason = (lat, month) => {
	if (lat > 0) {
		if(month > 1 && month < 5) {
			return 'spring';
		} else if (month > 4 && month < 8) {
			return 'summer';
		} else if (month > 7 && month > 10) {
			return 'fall';
		} else {
			return 'winter';
		}
	} else if (lat < 0) {
		if(month > 1 && month < 5) {
			return 'fall';
		} else if (month > 4 && month < 8) {
			return 'winter';
		} else if (month > 7 && month > 10) {
			return 'spring';
		} else {
			return 'summer';
		}
	}
}

const SeasonDisplay = props => {
	let season = getSeason(props.lat, new Date().getMonth());

	const returnFunction = (function seasonLogo(season) {
		switch(season) {
			case "winter":
				return "The Weather Outside is Frightful";
			case "spring":
				return "Mother Earth has Awoken";
			case "summer":
				return "Surf's Up, Son!!!";
			case "fall":
				return "It's Fall, Bitches!!!";
			default:
				return "Not Working..."
		}
	})();

	return <div>{returnFunction}</div>;
};

export default SeasonDisplay;
