import React from 'react';


const getSeason = (lat, month) => {
	if (lat > 0) {
		if(month > 1 && month < 5) {
			return 'spring';
		} else if (month > 4 && month < 8) {
			return 'summer';
		} else if (month > 7 && month > 10) {
			return 'fall';
		} else {
			return 'winter';
		}
	} else if (lat < 0) {
		if(month > 1 && month < 5) {
			return 'fall';
		} else if (month > 4 && month < 8) {
			return 'winter';
		} else if (month > 7 && month > 10) {
			return 'spring';
		} else {
			return 'summer';
		}
	}
}

const fallLogo = require('./images/fall.jpg')

const SeasonDisplay = props => {

	const season = getSeason(props.lat, new Date().getMonth());

	console.log(season);

	return (
		<div>
			<img src={fallLogo} alt={'Fall, Bitches!!!'} style={{ height: '400px', width: '400px' }} />
		</div>

	);
};

export default SeasonDisplay;
