import React from "react";

export default class ForecastAPI extends React.Component {

  async componentDidMount() {
    // weatherApi: 48f162a78e4f4a62865190945190412
		// *** <API> calls:
		// send request to website
		const response = await fetch("http://api.weatherapi.com/v1/forecast.json?key=48f162a78e4f4a62865190945190412&q=chicago&days=5")
		// convert results to JSON
		const data = await response.json();
		this.setState({ weather: data });
		console.log(this.state.weather);
  }

  render() {
    return (
      <h1>Hello from ForecastAPI</h1>
    );
  }
};
